import { HttpClient, HttpParams } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { ActivatedRoute, Params } from '@angular/router';
import { AuthService } from 'src/app/authorization/AuthService';
import { URLGlobal } from 'src/app/URLGlobal';
import { AddReviewRequest, BookDetails } from '../models';

@Component({
  selector: 'app-bookdetails',
  templateUrl: './bookdetails.component.html',
  styleUrls: ['./bookdetails.component.css']
})
export class BookdetailsComponent implements OnInit {

  book!: BookDetails
  bookId!: number
  isAdmin!: boolean
  imagePath!: string

  addReviewForm!: FormGroup

  constructor(
    private httpClient:HttpClient, 
    private authService:AuthService,
    private activatedRoute: ActivatedRoute) {}

  ngOnInit(): void {

    this.addReviewForm=new FormGroup({
      review: new FormControl(),
      rating: new FormControl()
    });


    this.activatedRoute.params.subscribe((params: Params)=> {
      this.bookId=params["bookId"];
      this.httpClient.get<BookDetails>(URLGlobal.libraryAppURL+"api/book/"+params["bookId"]).subscribe({
        next: (result)=>{
          console.log(result);
          if(result.imagePath!=null){
          this.imagePath=URLGlobal.libraryAppURL+"api/operations/image/?imagePath="+result.imagePath;
          }
          this.book=result;
        },
        error: (error)=>{
          console.log(error);
        }
      })});
  }

  addImage(event: Event){

    var fileInputElement=<HTMLInputElement>event.target!;
    var image=fileInputElement.files![0];

    const formData=new FormData();
    formData.append("image", image);

    //var extension=image.name.split('.').pop();

    var params=new HttpParams().append("bookId", this.bookId);

    this.httpClient.post(URLGlobal.libraryAppURL+"api/operations/upload", formData, { params }).subscribe({
        next: (result)=>{
          console.log(result)
        },
        error: (error)=>{
          console.log(error)
        }
    })

  }

  addReview() {

    var addReviewRequest=<AddReviewRequest>{};
    addReviewRequest.userId=sessionStorage.getItem("userId")!;
    addReviewRequest.bookId=this.bookId;
    addReviewRequest.rating=this.addReviewForm.controls["rating"].value;
    addReviewRequest.review=this.addReviewForm.controls["review"].value;


  }



}
<div class="bookdetailsgrid">

   <div *ngIf="this.imagePath" class="bookvisual" id="bookvisual">
      <img [src]="this.imagePath">

      <div class="imagebuttondiv">
         <p>Update image: </p>
         <input type="file" (change)="addImage($event)" value="Update image" class="imagebutton">
      </div>
   </div>

   <div class="bookinfo">
      <h3>{{book.title}}</h3>
      <div>
        <p>{{book.description}}</p>
      </div>
      <p>Author: {{book.author[0].name}}</p>   
   </div>
</div>


<div class="bookreviewsgrid">

   <form [formGroup]="addReviewForm">

      <label for="review">Review</label>
      <textarea formControlName="review" id="review"></textarea>

   <div class="radiobuttonsdiv">
      <p>Rating: </p>
      <input type="radio" formControlName="rating" id="1" value=1>
      <label for="1">1</label>
      <input type="radio" formControlName="rating" id="2" value=2>
      <label for="2">2</label>
      <input type="radio" formControlName="rating" id="3" value=3>
      <label for="3">3</label>
      <input type="radio" formControlName="rating" id="4" value=4>
      <label for="4">4</label>
      <input type="radio" formControlName="rating" id="5" value=5>
      <label for="5">5</label>
   </div>

   <button type="submit">Add</button>

   </form>

    <div *ngFor="let review of book.reviews">
        <p>User: {{review.user}}</p>
        <p>Rating: {{review.rating}}</p>
        <p *ngIf="review.review">Review: {{review.review}}</p>
    </div>

</div>
