import { HttpClient, HttpParams } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { URLGlobal } from 'src/app/URLGlobal';
import { Book } from '../models';

@Component({
  selector: 'app-bookdashboard',
  templateUrl: './bookdashboard.component.html',
  styleUrls: ['./bookdashboard.component.css']
})
export class BookdashboardComponent implements OnInit {

  books!: Book[]
  pageSize!: number;
  pageIndex!: number;

  constructor(private httpClient: HttpClient) {}

  ngOnInit(): void {
    this.pageSize=10;
    this.pageIndex=3;
    this.getData();
  }

  getData() {
    var params=new HttpParams().set("pageIndex", this.pageIndex).set("pageSize", this.pageSize)
    this.httpClient.get(URLGlobal.libraryAppURL+"api/book/all", { params }).subscribe({
        next: (result)=>{
          console.log(result);
        },
        error: (error)=>{
          console.log(error);
        }
    })};

    moveOnNextPage() {
      this.pageIndex+=1;
      this.getData();
    }

}
<div>

    <button (click)="moveOnNextPage()"></button>


</div>
