import { HttpClient, HttpParams } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { URLGlobal } from 'src/app/URLGlobal';
import { AuthorApiResult } from '../ApiResult';
import { Author } from '../models';

@Component({
  selector: 'app-addbook',
  templateUrl: './addbook.component.html',
  styleUrls: ['./addbook.component.css']
})
export class AddbookComponent implements OnInit {

  addBookForm!: FormGroup
  currentAuthors!: Author[]
  finalAuthors!: Set<string>;
  resultAuthorIds!: Set<number>;

  constructor(private httpClient:HttpClient) {}


  ngOnInit(): void {
    this.finalAuthors=new Set<string>();
    this.resultAuthorIds=new Set<number>();
    this.addBookForm=new FormGroup({
      title: new FormControl(''),
      description: new FormControl(''),
      authors: new FormControl(''),
      available: new FormControl('')
    });

    var authorsdiv=document.getElementById("authorsdiv");
    authorsdiv!.style.position="relative";
    var authorsField=document.getElementById("authors");
    authorsField!.style.display="block";

    this.addBookForm.get("authors")?.valueChanges.subscribe(
      (query)=>{
        var params=new HttpParams().set("query", query);
        this.httpClient.get<AuthorApiResult>(URLGlobal.libraryAppURL+"api/author/search", { params })
        .subscribe({
          next: (value)=>{
            console.log(value);

            document.getElementById("searchresults")?.remove();

            this.currentAuthors=value.data;
            var searchresultdiv=document.createElement("div");
            searchresultdiv.setAttribute("id", "searchresults");
            searchresultdiv.style.position="absolute";
            searchresultdiv.style.height="500px";
            searchresultdiv.style.width="500px";
            searchresultdiv.style.border="solid red";
            authorsdiv?.append(searchresultdiv);
            for(let i=0; i<this.currentAuthors.length; i++){
              var singleresultDiv=document.createElement("button");
              singleresultDiv.style.height="100px";
              singleresultDiv.style.width="100px";
              singleresultDiv.style.border="solid black";
              singleresultDiv.setAttribute("data-id", this.currentAuthors[i].authorId.toString());
              singleresultDiv.setAttribute("data-name", this.currentAuthors[i].name);
              singleresultDiv.addEventListener("click", (event)=>{
                this.finalAuthors.add((<HTMLButtonElement>event.target!).dataset["name"]!);
                this.resultAuthorIds.add(Number.parseInt((<HTMLButtonElement>event.target!).dataset["id"]!));
              })

              singleresultDiv.append(document.createElement("p").innerHTML=this.currentAuthors[i].name);
              searchresultdiv.append(singleresultDiv);
            }
          },
          error: (error)=>{
            document.getElementById("searchresults")?.remove();
          }
      })});
  }

  addBook(){
    var addBookRequest={
      title: this.addBookForm.controls["title"].value,
      description: this.addBookForm.controls["description"].value,
      authorIds: this.resultAuthorIds,
      available: this.addBookForm.controls["available"].value
    }

    this.httpClient.post(URLGlobal.libraryAppURL+"api/book/add", addBookRequest).subscribe({
      next: (result)=>{
        console.log(result);
      }, 
      error: (error)=>{
        console.log(error);
      }
    })

  }
}

<form [formGroup]="addBookForm">

    <label for="title">Title:</label>
    <input type="text" formControlName="title" id="title"/>

    <label for="description">Description:</label>
    <textarea type="text" formControlName="description" id="description"></textarea>

    <div id="authorsdiv">
    <label for="authors">Authors:</label>

        <div *ngFor="let author of finalAuthors">
            <button>{{author}}</button>
        </div>

    <input type="text" formControlName="authors" id="authors">
    </div>

    <label for="available">Available: </label>
    <input type="number" formControlName="available" id="available">

    <button type="submit" style="margin-left: 500px" (click)="addBook()">Add</button>

</form>
